static char pty_name[12];
int pty_master( void );

int pty_master( void )
{
  int i,fd;
  char* ptr;
  struct stat statbuff;
  static char ptychar[] = "pqrs";
  static char hexdigit[] = "0123456789abcdef";

  for( ptr = ptychar; *ptr!=0; ptr++ ) {
    strcpy( pty_name, "/dev/ttyXY" );
    pty_name[8] = *ptr;
    pty_name[9] = '0';

